def isStartPacket(a, b, c, d):
    charSet = set([a,b,c,d])
    # print(charSet)
    if len(charSet) == 4:
        return True
    return False

def isStartMessage(inputChars: list):
    charSet = set(inputChars)
    if len(charSet) == 14:
        return True
    return False

def calculateStartIndex(inputStream: str):
    for i in range(len(inputStream)):
        # print(i,':',i+3)
        check = isStartPacket(inputStream[i],inputStream[i+1],inputStream[i+2],inputStream[i+3])
        if check:
            # print('Start packet: ', i+4)
            # print('Chars Processed: ', i+3)
            # print(inputStream, ': ', i + 4)
            print('Packet', ': ', i + 4)
            break

def calculateStartMessage(inputStream:str):
    for i in range(len(inputStream)):
        # print(i,':',i+3)
        check = isStartMessage(inputStream[i:i+14])
        if check:
            # print('Start packet: ', i+4)
            # print('Chars Processed: ', i+3)
            # print(inputStream, ': ', i + 14)
            print('Message', ': ', i + 14)
            break

for stream in inputList:
    calculateStartIndex(stream)
    calculateStartMessage(stream)

calculateStartIndex(puzzleInput)
